function Activities_insert() {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Activities');
	if(!$arrPerm[1]) return false;

	$data = array();
	$data['Date'] = intval($_REQUEST['DateYear']) . '-' . intval($_REQUEST['DateMonth']) . '-' . intval($_REQUEST['DateDay']);
	$data['Date'] = parseMySQLDate($data['Date'], '1');
	$data['Description'] = $_REQUEST['Description'];
		if($data['Description'] == empty_lookup_value) { $data['Description'] = ''; }
	$data['Comment'] = br2nl($_REQUEST['Comment']);
	$data['Priority'] = $_REQUEST['Priority'];
		if($data['Priority'] == empty_lookup_value) { $data['Priority'] = ''; }
	$data['Assigned'] = $_REQUEST['Assigned'];
		if($data['Assigned'] == empty_lookup_value) { $data['Assigned'] = ''; }
	$data['Completed'] = $_REQUEST['Completed'];
		if($data['Completed'] == empty_lookup_value) { $data['Completed'] = ''; }
	$data['Stop'] = mysql_datetime($_REQUEST['Stop']);
		if($data['Stop'] == empty_lookup_value) { $data['Stop'] = ''; }

	// hook: Activities_before_insert
	if(function_exists('Activities_before_insert')) {
		$args = array();
		if(!Activities_before_insert($data, getMemberInfo(), $args)) { return false; }
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Activities', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// hook: Activities_after_insert
	if(function_exists('Activities_after_insert')) {
		$res = sql("select * from `Activities` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!Activities_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Activities', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) Activities_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

