		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Date)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Description)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Comment)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Priority)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Assigned)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Completed)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Stop)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		$templateCode = str_replace('<%%VALUE(Date)%%>', @date('m/d/Y', @strtotime(html_attr($row['Date']))), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Date)%%>', urlencode(@date('m/d/Y', @strtotime(html_attr($urow['Date'])))), $templateCode);
		if($AllowUpdate || $AllowInsert) {
			$templateCode = str_replace('<%%HTMLAREA(Description)%%>', '<textarea name="Description" id="Description" rows="5">' . html_attr($row['Description']) . '</textarea>', $templateCode);
		}else{
			$templateCode = str_replace('<%%HTMLAREA(Description)%%>', '<div id="Description" class="form-control-static">' . $row['Description'] . '</div>', $templateCode);
		}
		$templateCode = str_replace('<%%VALUE(Description)%%>', nl2br($row['Description']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Description)%%>', urlencode($urow['Description']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(Comment)%%>', safe_html($urow['Comment']), $templateCode);
		}else{
			$templateCode = str_replace('<%%VALUE(Comment)%%>', html_attr($row['Comment']), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(Comment)%%>', urlencode($urow['Comment']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Priority)%%>', safe_html($urow['Priority']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Priority)%%>', html_attr($row['Priority']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Priority)%%>', urlencode($urow['Priority']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Assigned)%%>', safe_html($urow['Assigned']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Assigned)%%>', html_attr($row['Assigned']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Assigned)%%>', urlencode($urow['Assigned']), $templateCode);
		$templateCode = str_replace('<%%CHECKED(Completed)%%>', ($row['Completed'] ? "checked" : ""), $templateCode);
		$templateCode = str_replace('<%%VALUE(Stop)%%>', app_datetime($row['Stop'], 'dt'), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Stop)%%>', urlencode(app_datetime($urow['Stop'], 'dt')), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Date)%%>', '1', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Date)%%>', urlencode('1'), $templateCode);
		$templateCode = str_replace('<%%HTMLAREA(Description)%%>', '<textarea name="Description" id="Description" rows="5"></textarea>', $templateCode);
		$templateCode = str_replace('<%%VALUE(Comment)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Comment)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Priority)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Priority)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Assigned)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Assigned)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%CHECKED(Completed)%%>', '', $templateCode);
		$templateCode = str_replace('<%%VALUE(Stop)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Stop)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans) {
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Activities');
	if($selected_id) {
		$jdata = get_joined_record('Activities', $selected_id);
		if($jdata === false) $jdata = get_defaults('Activities');
		$rdata = $row;
	}
	$templateCode .= loadView('Activities-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: Activities_dv
	if(function_exists('Activities_dv')) {
		$args=array();
		Activities_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>