		if($data['Amount'] == empty_lookup_value) { $data['Amount'] = ''; }
	$data['Logged'] = $_REQUEST['Logged'];
		if($data['Logged'] == empty_lookup_value) { $data['Logged'] = ''; }
	$data['Photo'] = PrepareUploadedFile('Photo', 512000,'jpg|jpeg|gif|png', false, '');
	if($data['Photo']) createThumbnail($data['Photo'], getThumbnailSpecs('CCJournal', 'Photo', 'tv'));
	if($data['Photo']) createThumbnail($data['Photo'], getThumbnailSpecs('CCJournal', 'Photo', 'dv'));
	if($data['Logged']== '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Logged': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
	if($_REQUEST['SelectedID']) {
		$res = sql("select * from CCJournal where id='" . makeSafe($_REQUEST['SelectedID']) . "'", $eo);
		if($row = db_fetch_assoc($res)) {
			if(!$data['Photo']) $data['Photo'] = $row['Photo'];
		}
	}

	// hook: CCJournal_before_insert
	if(function_exists('CCJournal_before_insert')) {
		$args = array();
		if(!CCJournal_before_insert($data, getMemberInfo(), $args)) { return false; }
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('CCJournal', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// hook: CCJournal_after_insert
	if(function_exists('CCJournal_after_insert')) {
		$res = sql("select * from `CCJournal` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!CCJournal_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('CCJournal', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) CCJournal_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

