		$args=array();
		if(!CCJournal_before_delete($selected_id, $skipChecks, getMemberInfo(), $args))
			return $Translation['Couldn\'t delete this record'];
	}

	sql("delete from `CCJournal` where `id`='$selected_id'", $eo);

	// hook: CCJournal_after_delete
