function Claim_insert() {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Claim');
	if(!$arrPerm[1]) return false;

	$data = array();
	$data['Date'] = intval($_REQUEST['DateYear']) . '-' . intval($_REQUEST['DateMonth']) . '-' . intval($_REQUEST['DateDay']);
	$data['Date'] = parseMySQLDate($data['Date'], '1');
	$data['Invoice'] = br2nl($_REQUEST['Invoice']);
	$data['Warehouse'] = $_REQUEST['Warehouse'];
		if($data['Warehouse'] == empty_lookup_value) { $data['Warehouse'] = ''; }
	$data['Tracking'] = $_REQUEST['Tracking'];
		if($data['Tracking'] == empty_lookup_value) { $data['Tracking'] = ''; }
	$data['Selecteds'] = $_REQUEST['Selecteds'];
		if($data['Selecteds'] == empty_lookup_value) { $data['Selecteds'] = ''; }
	$data['Shippingt'] = $_REQUEST['Shippingt'];
		if($data['Shippingt'] == empty_lookup_value) { $data['Shippingt'] = ''; }
	$data['Status'] = $_REQUEST['Status'];
		if($data['Status'] == empty_lookup_value) { $data['Status'] = ''; }
	if($data['Status']== '') {
		echo StyleSheet() . "\n\n<div class=\"alert alert-danger\">" . $Translation['error:'] . " 'Status': " . $Translation['field not null'] . '<br><br>';
		echo '<a href="" onclick="history.go(-1); return false;">'.$Translation['< back'].'</a></div>';
		exit;
	}

	// hook: Claim_before_insert
	if(function_exists('Claim_before_insert')) {
		$args = array();
		if(!Claim_before_insert($data, getMemberInfo(), $args)) { return false; }
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Claim', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// hook: Claim_after_insert
	if(function_exists('Claim_after_insert')) {
		$res = sql("select * from `Claim` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!Claim_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Claim', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) Claim_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

