			$templateCode = str_replace('<%%VALUE(URL)%%>', html_attr($row['URL']), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(URL)%%>', urlencode($urow['URL']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Customer)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Customer)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(VehicleType)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VehicleType)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(VIN)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(VIN)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Brand)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Brand)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Model)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Model)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Year)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Year)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(EngineNo)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(EngineNo)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Cylinder)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Cylinder)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Liters)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Liters)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Transmission)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Transmission)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(GEAR)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(GEAR)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(URL)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(URL)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans) {
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('URLEdit')) { document.getElementById('URLEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('URLEditLink')) { document.getElementById('URLEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('CustomerAuto');
	if($selected_id) {
		$jdata = get_joined_record('CustomerAuto', $selected_id);
		if($jdata === false) $jdata = get_defaults('CustomerAuto');
		$rdata = $row;
	}
	$templateCode .= loadView('CustomerAuto-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: CustomerAuto_dv
	if(function_exists('CustomerAuto_dv')) {
		$args=array();
		CustomerAuto_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
