	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('InvoiceDetails', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// hook: InvoiceDetails_after_insert
	if(function_exists('InvoiceDetails_after_insert')) {
		$res = sql("select * from `InvoiceDetails` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!InvoiceDetails_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('InvoiceDetails', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) InvoiceDetails_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

