		$jsReadOnly .= "\tjQuery('#order').replaceWith('<div class=\"form-control-static\" id=\"order\">' + (jQuery('#order').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#product').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#product_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#qty').replaceWith('<div class=\"form-control-static\" id=\"qty\">' + (jQuery('#qty').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	}elseif($AllowInsert) {
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
			$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(invoice)%%>', $combo_invoice->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(invoice)%%>', $combo_invoice->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(invoice)%%>', urlencode($combo_invoice->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(product)%%>', $combo_product->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(product)%%>', $combo_product->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(product)%%>', urlencode($combo_product->MatchText), $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array('invoice' => array('Invoice', 'Invoice'), 'product' => array('Products', 'Product'), );
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(invoice)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(order)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(product)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(qty)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(SubTotal)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(invoice)%%>', safe_html($urow['invoice']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(invoice)%%>', html_attr($row['invoice']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(invoice)%%>', urlencode($urow['invoice']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(order)%%>', safe_html($urow['order']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(order)%%>', html_attr($row['order']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(order)%%>', urlencode($urow['order']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(product)%%>', safe_html($urow['product']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(product)%%>', html_attr($row['product']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(product)%%>', urlencode($urow['product']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(qty)%%>', safe_html($urow['qty']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(qty)%%>', html_attr($row['qty']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(qty)%%>', urlencode($urow['qty']), $templateCode);
		$templateCode = str_replace('<%%VALUE(SubTotal)%%>', safe_html($urow['SubTotal']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(SubTotal)%%>', urlencode($urow['SubTotal']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(invoice)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(invoice)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(order)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(order)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(product)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(product)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(qty)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(qty)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(SubTotal)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(SubTotal)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans) {
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';

	$templateCode .= "\tproduct_update_autofills$rnd1 = function() {\n";
	$templateCode .= "\t\t\$j.ajax({\n";
	if($dvprint) {
		$templateCode .= "\t\t\turl: 'InvoiceDetails_autofill.php?rnd1=$rnd1&mfk=product&id=' + encodeURIComponent('".addslashes($row['product'])."'),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET'\n";
	} else {
		$templateCode .= "\t\t\turl: 'InvoiceDetails_autofill.php?rnd1=$rnd1&mfk=product&id=' + encodeURIComponent(AppGini.current_product{$rnd1}.value),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET',\n";
		$templateCode .= "\t\t\tbeforeSend: function() { \$j('#product$rnd1').prop('disabled', true); \$j('#productLoading').html('<img src=loading.gif align=top>'); },\n";
		$templateCode .= "\t\t\tcomplete: function() { " . (($arrPerm[1] || (($arrPerm[3] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm[3] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm[3] == 3)) ? "\$j('#product$rnd1').prop('disabled', false); " : "\$j('#product$rnd1').prop('disabled', true); ")."\$j('#productLoading').html(''); \$j(window).resize(); }\n";
	}
	$templateCode .= "\t\t});\n";
	$templateCode .= "\t};\n";
	if(!$dvprint) $templateCode .= "\tif(\$j('#product_caption').length) \$j('#product_caption').click(function() { /* */ product_update_autofills$rnd1(); });\n";


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('InvoiceDetails');
	if($selected_id) {
		$jdata = get_joined_record('InvoiceDetails', $selected_id);
		if($jdata === false) $jdata = get_defaults('InvoiceDetails');
		$rdata = $row;
	}
	$templateCode .= loadView('InvoiceDetails-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: InvoiceDetails_dv
	if(function_exists('InvoiceDetails_dv')) {
		$args=array();
		InvoiceDetails_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>