function Invoice_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = array(); // array of curl handlers for launching insert requests
	$eo = array('silentErrors' => true);
	$uploads_dir = realpath(dirname(__FILE__) . '/../' . $Translation['ImageFolder']);
	$safe_sid = makeSafe($source_id);

	// copy InvoiceDetails
	$res = sql("SELECT * FROM `InvoiceDetails` WHERE `invoice`='{$safe_sid}'", $eo);
	while($row = db_fetch_assoc($res)) {
		$data = array(
			'SelectedID' => $row['id'],
			'invoice' => $destination_id,
			'order' => $row['order'],
			'product' => $row['product'],
			'qty' => $row['qty'],
			'itemSale' => $row['itemSale'],
		);

		$ch = curl_insert_handler('InvoiceDetails', $data);
		if($ch !== false) $requests[] = $ch;
	}

	// launch requests, asynchronously
	curl_batch($requests);
}

