		$combo_PaymentStatus->SelectedData = $row['PaymentStatus'];
		$combo_Status->SelectedData = $row['Status'];
		$combo_related->SelectedData = $row['related'];
		$urow = $row; /* unsanitized data */
		$hc = new CI_Input();
		$row = $hc->xss_clean($row); /* sanitize data */
	} else {
