		$templateCode = str_replace('<%%URLVALUE(usrAdd)%%>', urlencode($urow['usrAdd']), $templateCode);
		$templateCode = str_replace('<%%VALUE(whenAdd)%%>', safe_html($urow['whenAdd']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(whenAdd)%%>', urlencode($urow['whenAdd']), $templateCode);
		$templateCode = str_replace('<%%VALUE(usrUpdated)%%>', safe_html($urow['usrUpdated']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(usrUpdated)%%>', urlencode($urow['usrUpdated']), $templateCode);
		$templateCode = str_replace('<%%VALUE(whenUpdated)%%>', safe_html($urow['whenUpdated']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(whenUpdated)%%>', urlencode($urow['whenUpdated']), $templateCode);
		$templateCode = str_replace('<%%VALUE(related)%%>', safe_html($urow['related']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(related)%%>', urlencode($urow['related']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(type)%%>', 'Quote', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(type)%%>', urlencode('Quote'), $templateCode);
		$templateCode = str_replace('<%%VALUE(number)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(number)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Date)%%>', '1', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Date)%%>', urlencode('1'), $templateCode);
		$templateCode = str_replace('<%%VALUE(Customer)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Customer)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(PaymentStatus)%%>', 'UNPAID', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(PaymentStatus)%%>', urlencode('UNPAID'), $templateCode);
		$templateCode = str_replace('<%%VALUE(AmountDUE)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(AmountDUE)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(AmountPAID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(AmountPAID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Balance)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Balance)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Status)%%>', 'OPEN', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Status)%%>', urlencode('OPEN'), $templateCode);
		$templateCode = str_replace('<%%VALUE(tax)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tax)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Total)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Total)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(usrAdd)%%>', '<%%creatorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(usrAdd)%%>', urlencode('<%%creatorUsername%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(whenAdd)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(whenAdd)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(usrUpdated)%%>', '<%%editorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(usrUpdated)%%>', urlencode('<%%editorUsername%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(whenUpdated)%%>', '<%%editingDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(whenUpdated)%%>', urlencode('<%%editingDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(related)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(related)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans) {
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
			$templateCode.="\n\tif(document.getElementById('EmailEdit')) { document.getElementById('EmailEdit').style.display='inline'; }";
			$templateCode.="\n\tif(document.getElementById('EmailEditLink')) { document.getElementById('EmailEditLink').style.display='none'; }";
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';

	$templateCode .= "\tCustomer_update_autofills$rnd1 = function() {\n";
	$templateCode .= "\t\t\$j.ajax({\n";
	if($dvprint) {
		$templateCode .= "\t\t\turl: 'Invoice_autofill.php?rnd1=$rnd1&mfk=Customer&id=' + encodeURIComponent('".addslashes($row['Customer'])."'),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET'\n";
	} else {
		$templateCode .= "\t\t\turl: 'Invoice_autofill.php?rnd1=$rnd1&mfk=Customer&id=' + encodeURIComponent(AppGini.current_Customer{$rnd1}.value),\n";
		$templateCode .= "\t\t\tcontentType: 'application/x-www-form-urlencoded; charset=" . datalist_db_encoding . "',\n";
		$templateCode .= "\t\t\ttype: 'GET',\n";
		$templateCode .= "\t\t\tbeforeSend: function() { \$j('#Customer$rnd1').prop('disabled', true); \$j('#CustomerLoading').html('<img src=loading.gif align=top>'); },\n";
		$templateCode .= "\t\t\tcomplete: function() { " . (($arrPerm[1] || (($arrPerm[3] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm[3] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm[3] == 3)) ? "\$j('#Customer$rnd1').prop('disabled', false); " : "\$j('#Customer$rnd1').prop('disabled', true); ")."\$j('#CustomerLoading').html(''); \$j(window).resize(); }\n";
	}
	$templateCode .= "\t\t});\n";
	$templateCode .= "\t};\n";
	if(!$dvprint) $templateCode .= "\tif(\$j('#Customer_caption').length) \$j('#Customer_caption').click(function() { /* */ Customer_update_autofills$rnd1(); });\n";


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	if( $_REQUEST['FilterField'][1]=='23' && $_REQUEST['FilterOperator'][1]=='<=>') {
		$templateCode.="\n<input type=hidden name=related value=\"" . html_attr((get_magic_quotes_gpc() ? stripslashes($_REQUEST['FilterValue'][1]) : $_REQUEST['FilterValue'][1]))."\">\n";
	}

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Invoice');
	if($selected_id) {
		$jdata = get_joined_record('Invoice', $selected_id);
		if($jdata === false) $jdata = get_defaults('Invoice');
		$rdata = $row;
	}
	$templateCode .= loadView('Invoice-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: Invoice_dv
	if(function_exists('Invoice_dv')) {
		$args=array();
		Invoice_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>