function Payroll_insert() {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Payroll');
	if(!$arrPerm[1]) return false;

	$data = array();
	$data['employee'] = $_REQUEST['employee'];
		if($data['employee'] == empty_lookup_value) { $data['employee'] = ''; }
	$data['date'] = intval($_REQUEST['dateYear']) . '-' . intval($_REQUEST['dateMonth']) . '-' . intval($_REQUEST['dateDay']);
	$data['date'] = parseMySQLDate($data['date'], '1');
	$data['start'] = parseCode('<%%creationTime%%>', true);
	$data['horas'] = $_REQUEST['horas'];
		if($data['horas'] == empty_lookup_value) { $data['horas'] = ''; }
	$data['comment'] = $_REQUEST['comment'];
		if($data['comment'] == empty_lookup_value) { $data['comment'] = ''; }
	$data['value'] = $_REQUEST['value'];
		if($data['value'] == empty_lookup_value) { $data['value'] = ''; }
	$data['status'] = $_REQUEST['status'];
		if($data['status'] == empty_lookup_value) { $data['status'] = ''; }
	if($data['status'] == '') $data['status'] = "UNPAID";

	// hook: Payroll_before_insert
	if(function_exists('Payroll_before_insert')) {
		$args = array();
		if(!Payroll_before_insert($data, getMemberInfo(), $args)) { return false; }
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Payroll', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// hook: Payroll_after_insert
	if(function_exists('Payroll_after_insert')) {
		$res = sql("select * from `Payroll` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!Payroll_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Payroll', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) Payroll_copy_children($recID, $_REQUEST['SelectedID']);

	return $recID;
}

