			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(employee)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(date)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(start)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(stop)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(horas)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(comment)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(value)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(status)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(employee)%%>', safe_html($urow['employee']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(employee)%%>', html_attr($row['employee']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(employee)%%>', urlencode($urow['employee']), $templateCode);
		$templateCode = str_replace('<%%VALUE(date)%%>', @date('m/d/Y', @strtotime(html_attr($row['date']))), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(date)%%>', urlencode(@date('m/d/Y', @strtotime(html_attr($urow['date'])))), $templateCode);
		$templateCode = str_replace('<%%VALUE(start)%%>', safe_html($urow['start']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(start)%%>', urlencode($urow['start']), $templateCode);
		$templateCode = str_replace('<%%VALUE(stop)%%>', safe_html($urow['stop']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(stop)%%>', urlencode($urow['stop']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(horas)%%>', safe_html($urow['horas']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(horas)%%>', html_attr($row['horas']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(horas)%%>', urlencode($urow['horas']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(comment)%%>', safe_html($urow['comment']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(comment)%%>', html_attr($row['comment']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(comment)%%>', urlencode($urow['comment']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(value)%%>', safe_html($urow['value']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(value)%%>', html_attr($row['value']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(value)%%>', urlencode($urow['value']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(status)%%>', safe_html($urow['status']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(status)%%>', html_attr($row['status']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(status)%%>', urlencode($urow['status']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(employee)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(employee)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(date)%%>', '1', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(date)%%>', urlencode('1'), $templateCode);
		$templateCode = str_replace('<%%VALUE(start)%%>', '<%%creationTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(start)%%>', urlencode('<%%creationTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(stop)%%>', '<%%editingTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(stop)%%>', urlencode('<%%editingTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(horas)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(horas)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(comment)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(comment)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(value)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(value)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(status)%%>', 'UNPAID', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(status)%%>', urlencode('UNPAID'), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans) {
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Payroll');
	if($selected_id) {
		$jdata = get_joined_record('Payroll', $selected_id);
		if($jdata === false) $jdata = get_defaults('Payroll');
		$rdata = $row;
	}
	$templateCode .= loadView('Payroll-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: Payroll_dv
	if(function_exists('Payroll_dv')) {
		$args=array();
		Payroll_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
