		if($data['AmountDue'] == empty_lookup_value) { $data['AmountDue'] = ''; }

	// hook: Subscriptions_before_insert
	if(function_exists('Subscriptions_before_insert')) {
		$args = array();
		if(!Subscriptions_before_insert($data, getMemberInfo(), $args)) { return false; }
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Subscriptions', backtick_keys_once($data), $error);
	if($error)
		die("{$error}<br><a href=\"#\" onclick=\"history.go(-1);\">{$Translation['< back']}</a>");

	$recID = db_insert_id(db_link());

	// hook: Subscriptions_after_insert
	if(function_exists('Subscriptions_after_insert')) {
		$res = sql("select * from `Subscriptions` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args=array();
		if(!Subscriptions_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Subscriptions', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(!empty($_REQUEST['SelectedID'])) Subscriptions_copy_children($recID, $_REQUEST['SelectedID']);

