	$templateCode = str_replace('<%%COMBOTEXT(Customer)%%>', $combo_Customer->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(Customer)%%>', urlencode($combo_Customer->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(LastPayment)%%>', ($selected_id && !$arrPerm[3] ? '<div class="form-control-static">' . $combo_LastPayment->GetHTML(true) . '</div>' : $combo_LastPayment->GetHTML()), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(LastPayment)%%>', $combo_LastPayment->GetHTML(true), $templateCode);
	$templateCode = str_replace('<%%COMBO(DueDate)%%>', ($selected_id && !$arrPerm[3] ? '<div class="form-control-static">' . $combo_DueDate->GetHTML(true) . '</div>' : $combo_DueDate->GetHTML()), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(DueDate)%%>', $combo_DueDate->GetHTML(true), $templateCode);
	$templateCode = str_replace('<%%COMBO(Status)%%>', $combo_Status->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Status)%%>', $combo_Status->SelectedData, $templateCode);

	/* lookup fields array: 'lookup field name' => array('parent table name', 'lookup field caption') */
	$lookup_fields = array('Customer' => array('Customers', 'Customer'), );
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent hspacer-md" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] && !$_REQUEST['Embedded']) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-success add_new_parent hspacer-md" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus-sign"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Customer)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Vendor)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(AccountID)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(LastPayment)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(DueDate)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Status)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Rate)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(AmountDue)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Customer)%%>', safe_html($urow['Customer']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Customer)%%>', html_attr($row['Customer']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Customer)%%>', urlencode($urow['Customer']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Vendor)%%>', safe_html($urow['Vendor']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Vendor)%%>', html_attr($row['Vendor']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Vendor)%%>', urlencode($urow['Vendor']), $templateCode);
		if($dvprint || (!$AllowUpdate && !$AllowInsert)) {
			$templateCode = str_replace('<%%VALUE(AccountID)%%>', safe_html($urow['AccountID']), $templateCode);
		}else{
			$templateCode = str_replace('<%%VALUE(AccountID)%%>', html_attr($row['AccountID']), $templateCode);
		}
		$templateCode = str_replace('<%%URLVALUE(AccountID)%%>', urlencode($urow['AccountID']), $templateCode);
		$templateCode = str_replace('<%%VALUE(LastPayment)%%>', @date('m/d/Y', @strtotime(html_attr($row['LastPayment']))), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(LastPayment)%%>', urlencode(@date('m/d/Y', @strtotime(html_attr($urow['LastPayment'])))), $templateCode);
		$templateCode = str_replace('<%%VALUE(DueDate)%%>', @date('m/d/Y', @strtotime(html_attr($row['DueDate']))), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DueDate)%%>', urlencode(@date('m/d/Y', @strtotime(html_attr($urow['DueDate'])))), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Status)%%>', safe_html($urow['Status']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Status)%%>', html_attr($row['Status']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Status)%%>', urlencode($urow['Status']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Rate)%%>', safe_html($urow['Rate']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Rate)%%>', html_attr($row['Rate']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Rate)%%>', urlencode($urow['Rate']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(AmountDue)%%>', safe_html($urow['AmountDue']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(AmountDue)%%>', html_attr($row['AmountDue']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(AmountDue)%%>', urlencode($urow['AmountDue']), $templateCode);
	}else{
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Customer)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Customer)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Vendor)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Vendor)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(AccountID)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(AccountID)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(LastPayment)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(LastPayment)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(DueDate)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(DueDate)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Status)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Status)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Rate)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Rate)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(AmountDue)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(AmountDue)%%>', urlencode(''), $templateCode);
	}

	// process translations
	foreach($Translation as $symbol=>$trans) {
		$templateCode = str_replace("<%%TRANSLATION($symbol)%%>", $trans, $templateCode);
	}

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if($_REQUEST['dvprint_x'] == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Subscriptions');
	if($selected_id) {
		$jdata = get_joined_record('Subscriptions', $selected_id);
		if($jdata === false) $jdata = get_defaults('Subscriptions');
		$rdata = $row;
	}
	$templateCode .= loadView('Subscriptions-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: Subscriptions_dv
	if(function_exists('Subscriptions_dv')) {
		$args=array();
		Subscriptions_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>