	if($selected_id) {
		$jdata = get_joined_record('TrackingCenter', $selected_id);
		if($jdata === false) $jdata = get_defaults('TrackingCenter');
		$rdata = $row;
	}
	$templateCode .= loadView('TrackingCenter-ajax-cache', array('rdata' => $rdata, 'jdata' => $jdata));

	// hook: TrackingCenter_dv
	if(function_exists('TrackingCenter_dv')) {
		$args=array();
		TrackingCenter_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}
?>