	$recID = db_insert_id(db_link());

	// hook: WHJournal_after_insert
	if(function_exists('WHJournal_after_insert')) {
		$res = sql("select * from `WHJournal` where `id`='" . makeSafe($recID, false) . "' limit 1", $eo);
